import React, { useState } from "react";

const LinearProgrammingSolver = () => {
  const [numVariables, setNumVariables] = useState(0);
  const [numEquations, setNumEquations] = useState(0);
  const [objectiveCoefficients, setObjectiveCoefficients] = useState([]);
  const [constraints, setConstraints] = useState([]);

  const handleVariableChange = (index, value) => {
    let updatedCoefficients = [...objectiveCoefficients];
    updatedCoefficients[index] = value;
    setObjectiveCoefficients(updatedCoefficients);
  };

  const initializeConstraintIfNeeded = (eqIndex) => {
    let updatedConstraints = [...constraints];
    if (!updatedConstraints[eqIndex]) {
      updatedConstraints[eqIndex] = { coefficients: Array(numVariables).fill(""), operator: "<=", rhs: "" };
    }
    return updatedConstraints;
  };

  const handleEquationChange = (eqIndex, varIndex, value) => {
    let updatedConstraints = initializeConstraintIfNeeded(eqIndex);
    updatedConstraints[eqIndex].coefficients[varIndex] = value;
    setConstraints(updatedConstraints);
    console.log(updatedConstraints);
  };

  const handleOperatorChange = (eqIndex, value) => {
    let updatedConstraints = initializeConstraintIfNeeded(eqIndex);
    updatedConstraints[eqIndex].operator = value;
    setConstraints(updatedConstraints);
    console.log(updatedConstraints);

  };

  const handleRHSChange = (eqIndex, value) => {
    let updatedConstraints = initializeConstraintIfNeeded(eqIndex);
    updatedConstraints[eqIndex].rhs = value;
    setConstraints(updatedConstraints);
    console.log(updatedConstraints);

  };

  return (
    <>
      <h1>Linear Programming Solver</h1>

      <div>
        <input
          type="number"
          placeholder="Number of Variables"
          onChange={(e) => setNumVariables(Number(e.target.value))}
        />
        <input
          type="number"
          placeholder="Number of Equations"
          onChange={(e) => setNumEquations(Number(e.target.value))}
        />
      </div>

      <div>
        <h2>Objective Function</h2>
        <div>
          <span>Z = </span>
          {Array.from({ length: numVariables }).map((_, index) => (
            <input
              key={index}
              type="text"
              placeholder={`x${index + 1}`}
              onChange={(e) => handleVariableChange(index, e.target.value)}
            />
          ))}
        </div>
      </div>

      <div>
        <h2>Constraints</h2>
        {Array.from({ length: numEquations }).map((_, eqIndex) => (
          <div key={eqIndex}>
            {Array.from({ length: numVariables }).map((_, varIndex) => (
              <input
                key={varIndex}
                type="text"
                placeholder={`x${varIndex + 1}`}
                onChange={(e) => handleEquationChange(eqIndex, varIndex, e.target.value)}
              />
            ))}
            <select onChange={(e) => handleOperatorChange(eqIndex, e.target.value)}>
              <option value="<=">≤</option>
              <option value=">=">≥</option>
              <option value="=">=</option>
            </select>
            <input
              type="text"
              placeholder="RHS"
              onChange={(e) => handleRHSChange(eqIndex, e.target.value)}
            />
          </div>
        ))}
      </div>

      <button style={{ width: "100%", marginTop: "10px", padding: "10px", backgroundColor: "blue", color: "white", border: "none", cursor: "pointer" }}>Solve</button>
      </>  );
};

export default LinearProgrammingSolver;
