import { useEffect, useState } from "react";
import { loadPyodide } from "pyodide";

function PythonExecutor() {
    const [pyodide, setPyodide] = useState(null);
    const [output, setOutput] = useState("");
    const [inputValue, setInputValue] = useState(5); // Default value

    useEffect(() => {
        const initPyodide = async () => {
            let py = await loadPyodide();
            setPyodide(py);
        };
        initPyodide();
    }, []);

    const runPython = async () => {
        if (!pyodide) return;

        // Define a Python function in Pyodide
        pyodide.runPython(`
def compute_factorial(n):
    import math
    return math.factorial(n)
        `);

        // Call the Python function with a parameter from React
        const result = pyodide.runPython(`compute_factorial(${inputValue})`);
        setOutput(result);
    };

    return (
        <div>
            <input
                type="number"
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
            />
            <button onClick={runPython}>Compute Factorial</button>
            <p>Output: {output}</p>
        </div>
    );
}

export default PythonExecutor;
